import { createApp } from 'vue';
import App from './App.vue';
import router from './router';
import vuetify from './plugins/vuetify';
import VueTheMask from 'vue-the-mask';
import axios from 'axios';

// Configurações do Axios
axios.defaults.headers.common['X-Requested-With'] = 'XMLHttpRequest';
axios.defaults.baseURL = 'http://127.0.0.1:8000';
axios.defaults.withCredentials = true;  // Importante para enviar cookies

const app = createApp(App);

// Configurando o CSRF token para o Axios antes de montar a aplicação
axios.get('/csrf-cookie').then(response => {
    console.log('CSRF token has been set.');
    // Configurar o token CSRF para todas as requisições após obtê-lo
    axios.interceptors.request.use(config => {
        config.headers['X-CSRF-TOKEN'] = response.data.csrf_token; // Ajuste baseado na resposta real
        return config;
    });

    const app = createApp(App);
    app.use(router);
    app.use(vuetify);
    app.use(VueTheMask);
    app.mount('#app');
}).catch(error => {
    console.error('Error fetching CSRF token:', error);
});
