import axios from 'axios';
export default {
  data() {
    return {
      search: {
        cpf: '',
        name: '',
        phone: ''
      },
      records: [],
      headers: [{
        text: 'CPF',
        value: 'cpf'
      }, {
        text: 'Nome',
        value: 'name'
      }, {
        text: 'Telefone',
        value: 'phone'
      }, {
        text: 'Ações',
        value: 'actions',
        sortable: false
      }]
    };
  },
  methods: {
    fetchResults() {
      axios.get('/registrations', {
        params: this.search
      }).then(response => {
        this.records = response.data.data;
      }).catch(error => {
        console.error('Erro ao buscar registros:', error);
      });
    }
  },
  mounted() {
    this.fetchResults(); // Carregar registros ao montar o componente
  }
};
