import "core-js/modules/es.array.push.js";
import { useVuelidate } from '@vuelidate/core';
import { VueTheMask } from 'vue-the-mask';
import axios from 'axios';
export default {
  name: 'CreateRecord',
  directives: {
    mask: VueTheMask
  },
  data() {
    return {
      valid: true,
      record: {
        name: '',
        social_name: '',
        cpf: '',
        father_name: '',
        mother_name: '',
        phone: '',
        email: ''
      },
      errorMessage: '',
      rules: {
        required: value => !!value || 'Campo obrigatório.',
        email: value => /.+@.+\..+/.test(value) || 'E-mail deve ser válido.',
        cpf: value => /^\d{3}\.\d{3}\.\d{3}-\d{2}$/.test(value) || 'CPF deve ser válido.',
        phone: value => /^\(\d{2}\) \d{4,5}-\d{4}$/.test(value) || 'Telefone deve ser válido.'
      }
    };
  },
  setup() {
    return {
      v$: useVuelidate()
    };
  },
  methods: {
    submitData() {
      axios.post('/registrations', this.record).then(() => {
        this.$router.push({
          name: 'AddAddress'
        }); // Supondo que a rota para adicionar endereço se chama 'AddAddress'
      }).catch(error => {
        if (error.response) {
          this.errorMessage = error.response.data.message || 'Erro ao submeter os dados.';
        } else if (error.request) {
          this.errorMessage = 'Nenhuma resposta recebida do servidor.';
        } else {
          this.errorMessage = 'Erro ao configurar a requisição: ' + error.message;
        }
      });
    }
  }
};
