import { useVuelidate } from '@vuelidate/core';
import { VueTheMask } from 'vue-the-mask';
import axios from 'axios';
export default {
  name: 'CreateRecord',
  directives: {
    mask: VueTheMask
  },
  data() {
    return {
      valid: true,
      record: {
        name: '',
        social_name: '',
        cpf: '',
        father_name: '',
        mother_name: '',
        phone: '',
        email: ''
      },
      rules: {
        required: value => !!value || 'Campo obrigatório.',
        email: value => /.+@.+\..+/.test(value) || 'E-mail deve ser válido.',
        cpf: value => /^\d{3}\.\d{3}\.\d{3}-\d{2}$/.test(value) || 'CPF deve ser válido.',
        phone: value => /^\(\d{2}\) \d{4,5}-\d{4}$/.test(value) || 'Telefone deve ser válido.'
      }
    };
  },
  setup() {
    return {
      v$: useVuelidate()
    };
  },
  methods: {
    submitData() {
      axios.post('/registrations', this.record).then(response => {
        console.log('Data submitted successfully', response.data);
      }).catch(error => {
        if (error.response) {
          // A resposta foi recebida e o servidor respondeu com um status fora do intervalo 2xx
          console.error('Failed to submit data', error.response.data);
        } else if (error.request) {
          // A requisição foi feita mas nenhuma resposta foi recebida
          console.error('No response received', error.request);
        } else {
          // Algo aconteceu na configuração da requisição que disparou um erro
          console.error('Error', error.message);
        }
        console.error('Config details:', error.config);
      });
    }
  }
};
