import "core-js/modules/es.array.push.js";
import axios from 'axios';
export default {
  data() {
    return {
      search: {
        cpf: '',
        name: '',
        phone: ''
      },
      records: [],
      errorMessage: '',
      headers: [{
        text: 'CPF',
        value: 'cpf'
      }, {
        text: 'Nome',
        value: 'name'
      }, {
        text: 'Telefone',
        value: 'phone'
      }, {
        text: 'Endereço Residencial',
        value: 'residential_address'
      }, {
        text: 'Endereço Comercial',
        value: 'commercial_address'
      }, {
        text: 'Ações',
        value: 'actions',
        sortable: false
      }]
    };
  },
  methods: {
    fetchResults() {
      axios.get('/registrations', {
        params: this.search
      }).then(response => {
        this.records = response.data.data;
        this.errorMessage = '';
      }).catch(error => {
        this.errorMessage = 'Erro ao buscar registros: ' + (error.response ? error.response.data.message : error.message);
      });
    },
    formatAddress(addresses, type) {
      const address = addresses.find(addr => addr.type === type);
      return address ? `${address.street}, ${address.number}${address.complement ? ', ' + address.complement : ''} - ${address.neighborhood}, ${address.city} - ${address.state}` : 'N/A';
    },
    openDetails(item) {
      this.$router.push({
        name: 'DetailPage',
        params: {
          id: item.id
        }
      });
    }
  },
  mounted() {
    this.fetchResults(); // Carregar registros ao montar o componente
  }
};
